    private void jSearchActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jSearchActionPerformed
        int checkid=0;
        String id=txt_id.getText();
        try{
            Connection c=Connectinprovider.connect();
            Statement s=c.createStatement();
            ResultSet r=s.executeQuery("select *from member where id='"+id+"'");
            while(r.next()){
            checkid=1;
            txt_id.setEditable(false);
            txt_name.setText(r.getString(2));
            txt_mobile_number.setText(r.getString(3));
            txt__email.setText(r.getString(4));
            txt_gender.setText(r.getString(5));
            txt_gender.setEditable(false);
            txt_father_name.setText(r.getString(6));
            txt_mother_name.setText(r.getString(7));
            txt_gymtime.setText(r.getString(8));
            txt_gymtime.setEditable(false);
            txt_city.setText(r.getString(9));
            txt_age.setText(r.getString(10));
            txt_amount.setText(r.getString(11));
            
            }
            if(checkid==0)
                JOptionPane.showMessageDialog(null, "Member ID doesn't exist");    
        }
        catch(Exception ex)
        {
            JOptionPane.showMessageDialog(null,ex);
        }
        
    }//GEN-LAST:event_jSearchActionPerformed

    private void SaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SaveActionPerformed
       String id=txt_id.getText();
       String name=txt_name.getText();
       String mobilenumber=txt_mobile_number.getText();
       String email=txt__email.getText();
       String fathername=txt_father_name.getText();
       String mothername=txt_mother_name.getText();
       String city=txt_city.getText();
       String age=txt_age.getText();
       String amount=txt_amount.getText();
       try
       {
           Connection c=Connectinprovider.connect();
           PreparedStatement p=c.prepareStatement("update member set membername=?,mobilenumber=?,email=?,fatherName=?,motherName=?,city=?,age=?,amount=? where id=?");
           p.setString(1, name);
           p.setString(2, mobilenumber);
           p.setString(3, email);
           p.setString(4, fathername);
           p.setString(5, mothername);
           p.setString(6, city);
           p.setString(7, age);
           p.setString(8, amount);
           p.setString(9, id);
           p.executeUpdate();
           JOptionPane.showMessageDialog(null, "successfullt ubdated");
           setVisible(false);
           new updatedeletemember().setVisible(true);
           
       }
       catch(Exception ex)
       {
           JOptionPane.showMessageDialog(null, ex);
       }
       
    }//GEN-LAST:event_SaveActionPerformed

